import React, { useState } from 'react';
import { Link, NavLink } from 'react-router-dom';

import menu from './../data/menu';

function Dropdown() {
  const [open, setOpen] = useState(false);
  const handleOpen = () => setOpen(!open);
  const handleSubOpen = () => setOpen(!open);

  return (
		<>
			{
				menu.map(m => (<>
					{
						m.children ?
						(
							<li key={m.id} className={open ? "dropdown" : ""}>
								<Link onClick={() => handleOpen(!open)}>
									<span>{m.name}</span>
									{!open ? <i className="bi bi-chevron-right"></i> : <i className="bi bi-chevron-down"></i>}
							  </Link>
							  {
							  	open ? (<ul className="dropdown-active">
									{
										m.children.map(mc => (<>
										{
											mc.children ? (<li className={open ? "dropdown" : ""}>
												<Link key={mc.id}
													to={mc.path}
													onClick={() => handleSubOpen(!open)}
												>
													<span>{mc.name}</span>
													{!open ? <i className="bi bi-chevron-right"></i> : <i className="bi bi-chevron-down"></i>}
												</Link>
												{
													open ? (<ul className={open ? "dropdown-active" : ""}>
													{
														mc.children.map(mcc => (
															<li key={mcc.id}>
																<Link>{mcc.name}</Link>
															</li>
														))
													}
												</ul>) : (<></>)
												}
											</li>) : (<li>
												<Link to={mc.path}>{mc.name}</Link>
											</li>)
										}
									</>))
									}
								</ul>) : (<></>)
							  }
							</li>
						)
						:
						(<li><Link to={m.path}>{m.name}</Link></li>)
					}
				</>))
			}
		</>
	)
}

function Navbar() {
	const [toggle, setToggle] = useState(false);
  const handleToggle = () => setToggle(!toggle);

	return (
		<nav id="navbar" className={!toggle ? "navbar" : "navbar navbar-mobile"}>
			<ul>
				<Dropdown />
				<li><Link to="/login">Login</Link></li>
				<li><Link to="/logout">Logout</Link></li>
			</ul>
			{ !toggle ?
 				(<i onClick={handleToggle} className="bi mobile-nav-toggle bi-list"></i>)
				:
				(<i onClick={handleToggle} className="bi mobile-nav-toggle bi-x"></i>)
			}
		</nav>
	)
}

function Header() {
	return (
  	<header id="header" className="fixed-top d-flex align-items-center">
			<div className="container d-flex align-items-center justify-content-between">
				<div className="logo">
					<h1 className="text-light">
						<NavLink
							to="/"
							className={({ isActive, isPending }) =>
    isPending ? "pending" : isActive ? "active" : ""
  }
						>Serenity</NavLink>
					</h1>
					{/*<Link to="/">
						<img src="../assets/img/logo.png" alt="" className="img-fluid" />
					</Link>*/}
				</div>
				<Navbar />
			</div>
		</header>
	)
}

export default Header;

