      </div>
    </>
  );
};

const Card = ({ item }) => {
  return (
    <>
      <div className="container-fluid">
        <div className="row justify-content-center">
          {item.map((val) => {
            return (
              <div
                className="col-md-4 col-sm-6 card my-3 py-3 border-0"
                key={val.id}
              >
                <div className="card-img-top text-center">
                  <img src={val.img} alt={val.title} className="photo w-75" />
                </div>
                <div className="card-body">
                  <div className="card-title fw-bold fs-4">
                    {val.title} &nbsp;&nbsp;&nbsp;&nbsp;--&nbsp;&nbsp;
                    {val.price}
                  </div>
                  <div className="card-text">{val.desc}</div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
};

function Food() {
	const [item, setItem ] = useState([foods]);
	const menuItems = [...new Set(foods.map((val) => val.category))];

  const filterItem = (curcat) => {
    const newItem = foods.filter((newval) => {
      return newval.category === curcat;
    });
    setItem(newItem);
  };

	return (
		<main id="main">
			<Breadcrumb page="Food" />

			<div className="container">
				<ButtonFilter />
				<div className="row">
				{
					foods.map(food => (
				 		<div className="col-md-6 mb-3">
							<div className="card">
						    <img src={food.img} alt=""/>
							  <div className="card-body">
				      		<h3>{food.title}</h3>
									<small>{food.price}</small>
									<p>{food.desc}</p>
								</div>
							</div>
						</div>
					))
				}
				</div>
			</div>
 		</main>
	)
}

export default Food;
