import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';

export const menuItemsData = [
  {
    title: "Home",
    url: "/",
  },
  {
    title: "Services",
    url: "/services",
    submenu: [
      {
        title: "Web Design",
        url: "web-design",
      },
      {
        title: "Web Development",
        url: "web-dev",
        submenu: [
          {
            title: "Frontend",
            url: "frontend",
          },
          {
            title: "Backend",
            submenu: [
              {
                title: "NodeJS",
                url: "node",
              },
              {
                title: "PHP",
                url: "php",
              },
            ],
          },
        ],
      },
      {
        title: "SEO",
        url: "seo",
      },
    ],
  },
  {
    title: "About",
    url: "/about",
  },
];

function Dropdown ({ submenus, dropdown, depthLevel }) {
  depthLevel = depthLevel + 1;
  const dropdownClass = depthLevel > 1 ? "dropdown-submenu" : "";

  return (
    <ul className={`dropdown ${dropdownClass} ${dropdown ? "show" : ""}`}>
      {submenus.map((submenu, index) => (
        <MenuItems items={submenu} key={index} depthLevel={depthLevel} />
      ))}
    </ul>
  );
};

function MenuItems({ items, depthLevel }) {
  const [dropdown, setDropdown] = useState(false);
  let ref = useRef();

  useEffect(() => {
    const handler = (event) => {
      if (dropdown && ref.current && !ref.current.contains(event.target)) {
        setDropdown(false);
      }
    };
    document.addEventListener("mousedown", handler);
    document.addEventListener("touchstart", handler);
    return () => {
      // Cleanup the event listener
      document.removeEventListener("mousedown", handler);
      document.removeEventListener("touchstart", handler);
    };
  }, [dropdown]);

  const onMouseEnter = () => {
    setDropdown(true);
  };

  const onMouseLeave = () => {
    setDropdown(false);
  };

  const toggleDropdown = () => {
    setDropdown((prev) => !prev);
  };

  const closeDropdown = () => {
    dropdown && setDropdown(false);
  };

  return (
    <li
      className="menu-items"
      ref={ref}
      onMouseEnter={onMouseEnter}
      onMouseLeave={onMouseLeave}
      onClick={closeDropdown}>
      {items.url && items.submenu ? (
        <>
          <button
            type="button"
            aria-haspopup="menu"
            aria-expanded={dropdown ? "true" : "false"}
            onClick={() => toggleDropdown()}>
            <Link to={items.url}>{items.title}</Link>
            {depthLevel > 0 ? <span>&raquo;</span> : <span className="arrow" />}
          </button>
          <Dropdown
            depthLevel={depthLevel}
            submenus={items.submenu}
            dropdown={dropdown}
          />
        </>
      ) : !items.url && items.submenu ? (
        <>
          <button
            type="button"
            aria-haspopup="menu"
            aria-expanded={dropdown ? "true" : "false"}>
            {items.title}
            {depthLevel > 0 ? <span>&raquo;</span> : <span className="arrow" />}
          </button>
          <Dropdown
            depthLevel={depthLevel}
            submenus={items.submenu}
            dropdown={dropdown}
          />
        </>
      ) : (
        <Link to={items.url}>{items.title}</Link>
      )}
    </li>
  );
};

function Navbar() {
	const depthLevel = 0;

  return (
    <nav className="desktop-nav">
      <ul className="menus">
        {menuItemsData.map((menu, index) => {
          return <MenuItems items={menu} key={index} depthLevel={depthLevel} />;
        })}
      </ul>
    </nav>
  );
}

function Header() {
	const [toggle, setToggle] = useState(false);
  const [dropdown, setDropdown] = useState(false);
  const handleToggle = () => setToggle(!toggle);
  const handleDropdown = (initVal) => setDropdown(!dropdown);

	return (
  		<header id="header" className="fixed-top d-flex align-items-center">
				<div className="container d-flex align-items-center justify-content-between">

			      <div className="logo">
			        <h1 className="text-light">
								<Link to="/">Serenity</Link>
							</h1>
							{/*
						  	<Link to="/">
									<img src="../assets/img/logo.png" alt="" className="img-fluid" />
								</Link>
 	  					*/}
			      </div>

			      <nav id="navbar" className={!toggle ? 'navbar' : 'navbar navbar-mobile'}>
 			 				{toggle ?
			        (<ul className={dropdown ? 'dropdown-active' : null }>
			         	<li><Link to="/">Home</Link></li>
							  <li><Link to="/blog">Blog</Link></li>

			          <li className="dropdown">
									<Link onClick={() => handleDropdown(1)}>
										<span>About</span>
										<i
											className={`bi ${!dropdown ? 'bi-chevron-right' : 'bi-chevron-down'}`}
										></i>
									</Link>

			            {dropdown ? (<ul className={dropdown ? 'dropdown-active' : null}>
			              <li><Link to="/about">About</Link></li>
			              <li><Link to="/about/team">Team</Link></li>
										<li className="dropdown">
											<Link onClick={() => handleDropdown(2)}>
												<span>Deep Drop Down</span>
												<i className={`bi ${dropdown ? 'bi-chevron-right' : 'bi-chevron-down'}`}></i>
											</Link>

											 {dropdown ? (<ul className={dropdown ? 'dropdown-active' : null}>
				                  <li><Link to="#">Deep Drop Down 1</Link></li>
				                  <li><Link to="#">Deep Drop Down 2</Link></li>
				                  <li><Link to="#">Deep Drop Down 3</Link></li>
				                  <li><Link to="#">Deep Drop Down 4</Link></li>
				                  <li><Link to="#">Deep Drop Down 5</Link></li>
				                </ul>) : (<></>)}

										</li>
			            </ul>
    						 ) : (<></>)
 	 							}
			          </li>

			          <li><Link to="/foods">Foods</Link></li>
			          <li><Link to="/services">Services</Link></li>
			          <li><Link to="/pricing">Pricing</Link></li>
			          <li><Link to="/portfolio">Portfolio</Link></li>
			          <li><Link to="/contact">Contact</Link></li>

			          <li><Link to="about" className="getstarted">Get Started</Link></li>
			        </ul>
							) : <></>}
 							<i onClick={handleToggle} className={`bi mobile-nav-toggle ${!toggle ? 'bi-list' : 'bi-x'}`}></i>
			      </nav>

				</div>
 			</header>
	)
}

export default Header;
