import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Search from '../components/widget/Search';
import Category from '../components/widget/Category';
import RecentPost from '../components/widget/RecentPost';
import Tag from '../components/widget/Tag';
import Breadcrumb from '../components/widget/Breadcrumb';

import data from './../posts.js';

function Blog() {
	const [posts, setPosts] = useState(data);
 	const [page, setPage] = useState(0);

  const nextPage = () => {
    setPage((oldPage) => {
      let nextPage = oldPage + 1
      if (nextPage > data.length - 1) {
        nextPage = 0
      }
      return nextPage
    })
  }

  const prevPage = () => {
    setPage((oldPage) => {
      let prevPage = oldPage - 1
      if (prevPage < 0) {
        prevPage = data.length - 1
      }
      return prevPage
    })
  }

  const handlePage = (index) => {
    setPage(index)
  }

  return (
		<main id="main">
 	 		<Breadcrumb page="Blog" description="" />

		  <section id="blog" className="blog">
		    <div className="container" data-aos="fade-up">

		      <div className="row">
		        <div className="col-lg-8 entries">

				      <div className="row">
								{posts.map(post => (
				      	<div key={post.id} className="col-6">
				          <article className="entry">

				            <div className="entry-img">
				              <img src={`assets/img/blog/${post.picture}`} alt="" className="img-fluid" />
				            </div>

				            <h2 className="entry-title">
				              <Link to="">{post.title}</Link>
				            </h2>

				            <div className="entry-meta">
				              <ul>
				                <li className="d-flex align-items-center">
													<i className="bi bi-person"></i> <a href="#">{post.created_by.name}</a>
												</li>
				                <li className="d-flex align-items-center">
													<i className="bi bi-clock"></i> <a href="#"><time dateTime={post.created_at}>{post.created_at}</time></a>
												</li>
				                <li className="d-flex align-items-center">
 													<i className="bi bi-chat-dots"></i>	<a href="#">{post.comments_count} Comments</a>
												</li>
				              </ul>
				            </div>

				            <div className="entry-content">
				              <div className="read-more">
				                <Link href="blog-single">Read More</Link>
				              </div>
				            </div>

				          </article>
							  </div>
	 							))}

						 	</div>

							<div className="mb-5 blog-pagination">
							  <ul className="justify-content-center">
									{data.map((item, index) => (<li key={index}>{item}</li>))}
							  </ul>
							</div>

		        </div>

						<div className="col-lg-4">
						  <div className="sidebar">
								<Search />
								<Category />
								<RecentPost />
								<Tag />
						  </div>
						</div>

		      </div>
		    </div>
		  </section>
		</main>
	)
}

export default Blog;
