import React, { useState, useMemo } from 'react';
import { Link } from 'react-router-dom';

import Sidebar from '../components/Sidebar';
import Pagination from '../components/widget/Pagination';

import data from './../posts.js';

let pageSize = 4;

function Blog() {
	const [posts, setPosts] = useState(data);
	const [currentPage, setCurrentPage] = useState(1);

  const currentPostsData = useMemo(() => {
    const firstPageIndex = (currentPage - 1) * pageSize;
    const lastPageIndex = firstPageIndex + pageSize;
    return data.slice(firstPageIndex, lastPageIndex);
  }, [currentPage]);

  return (
		<main id="main">
 	 		<Breadcrumb page="Blog" description="" />

		  <section id="blog" className="blog">
		    <div className="container" data-aos="fade-up">
		      <div className="row">
		        <div className="col-lg-8 entries">
   						<div className="row">
								{
							  	currentPostsData.map(post => (
						      	<div key={post.id} className="col-6">
						          <article className="entry">

						            <div className="entry-img">
						              <img src={`assets/img/blog/${post.picture}`} alt={post.picture_description} className="img-fluid" />
						            </div>

						            <h2 className="entry-title">
						              <Link to={`blog/${post.id}`}>{post.title}</Link>
						            </h2>

						            <div className="entry-meta">
						              <ul>
						                <li className="d-flex align-items-center">
															<i className="bi bi-person"></i> <a href="#">{post.created_by.name}</a>
														</li>
						                <li className="d-flex align-items-center">
															<i className="bi bi-clock"></i> <a href="#"><time dateTime={post.created_at}>{post.created_at}</time></a>
														</li>
						                <li className="d-flex align-items-center">
		 													<i className="bi bi-chat-dots"></i>	<a href="#">{post.comments_count} Comments</a>
														</li>
						              </ul>
						            </div>
						            <div className="entry-content">
						              <div className="read-more">
						                <Link to={`blog/${post.id}`}>Read More</Link>
						              </div>
						            </div>

						          </article>
									  </div>
 									))
  							}
							</div>

	  					<div className="mb-5 blog-pagination">
						    <ul className="justify-content-center">
							   	<Pagination
										currentPage={currentPage}
						        totalCount={data.length}
						        pageSize={pageSize}
						        onPageChange={page => setCurrentPage(page)}
						      />
					 		  </ul>
			        </div>

						</div>

						<div className="col-lg-4">
						  <div className="sidebar">
								<Search />
								<Category />
								<RecentPost />
								<Tag />
						  </div>
 						</div>

					</div>

 		    </div>
		  </section>
		</main>
	)
}

export default Blog;


