import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const menu = [
	{
		id: 1,
		name: 'home'
	},
	{
		id: 2,
		name: 'about'
	},
	{
		id: 3,
		name: 'blog'
	},
	{
		id: 4,
		name: 'contact'
	},
	{
		id: 5,
		name: 'services'
	},
	{
		id: 6,
		name: 'pricing'
	},
	{
		id: 7,
		name: 'portfolio'
	}
];

function Navbar() {
  const handleToggle = () => setToggle(!toggle);
	return (
		<nav id="navbar" className="navbar">
			<i className="bi bi-list mobile-nav-toggle"></i>
			<ul>
			{
	 			menu.map(m =>(<li key={m.id}><Link to={m.name}>{`${m.name[0].toUpperCase() + m.name.slice(1)}`}</Link></li>))
			}
			</ul>
 			<i onClick={handleToggle} className={`bi mobile-nav-toggle ${!toggle ? 'bi-list' : 'bi-x'}`}></i>
		</nav>
	)
}

function Header() {
	const [toggle, setToggle] = useState(false);
  const [dropdown, setDropdown] = useState(false);
  const handleDropdown = () => setDropdown(!dropdown);

	return (
  	<header id="header" className="fixed-top d-flex align-items-center">
			<div className="container d-flex align-items-center justify-content-between">
					<div className="logo">
						<h1 className="text-light"><Link to="/">Serenity</Link></h1>
						{/*<Link to="/">
							<img src="../assets/img/logo.png" alt="" className="img-fluid" />
						</Link>*/}
					</div>
					<Navbar />
			</div>
		</header>
	)
}

export default Header;

