import { Link } from 'react-router-dom';
import posts from './../../data/posts';

export default function Tag() {
	const tags = [...new Set(posts.map(post => post.tag.split(',')))];

	// console.log(tags)

	function removeDuplicate() {
		let unique = tags.reduce((a, b, c) => { if(a.indexOf(b) < 0 && a.indexOf(b) === c) a.push(b); return a}, [])
		return unique;
	}

	let tagg = removeDuplicate(tags);

	console.log(tagg)

	return (
		<>
	    <h3 className="sidebar-title">Tags</h3>
	    <div className="sidebar-item tags">
	      <ul>
	      {
	      	tagg.map((tag, idx) => (<li><Link>{tag}</Link></li>))
	      }
	      </ul>
	    </div>
		</>
	)
}

