		type: 'App',
	},
	{
	 	id: 7,
		image: 'assets/img/portfolio/portfolio-7.jpg',
		type: 'Card',
	},
	{
	 	id: 8,
		image: 'assets/img/portfolio/portfolio-8.jpg',
		type: 'Card',
	},
	{
	 	id: 9,
		image: 'assets/img/portfolio/portfolio-9.jpg',
		type: 'Web',
	}
];

function ListFilter({ filterItem, setItem, portfolioItems }) {
	return (
		<>
			{
				portfolioItems.map((val, id) => (
					<li
						key={id}
						onClick={() => filterItem(val)}
						className="filter-active"
					>{val}</li>
				))
			}
			<li
				onClick={() => setItem(foods)}
				className="filter-active"
			>All</li>
		</>
	)
}

function Portfolio() {
	let liststype = listsportfolio.map((item, index) => item.type)

	let duplicates = liststype.reduce(function(acc, el, i, arr) {
		if (arr.indexOf(el) !== i && acc.indexOf(el) < 0) {
			acc.push(el);
		}
		return acc;
	}, []);

	return (
		<main id="main">
			<Breadcrumb page="Portfolio" description="Est dolorum ut non facere possimus quibusdam eligendi voluptatem. Quia id aut similique quia voluptas sit quaerat debitis. Rerum omnis ipsam aperiam consequatur laboriosam nemo harum praesentium." />

		  <section id="portfolio" className="portfolio">
		    <div className="container">

		      <div className="row">
		        <div className="col-lg-12 d-flex justify-content-center">
		          <ul id="portfolio-flters">
								<ListFilter />
		          </ul>
		        </div>
		      </div>

		      <div className="row portfolio-container">

		      </div>
		    </div>
		  </section>

		</main>
	)
}

export default Portfolio;


