import { useState } from 'react';
import Breadcrumb from './../components/widget/Breadcrumb';
import foods from './../foods';

const Buttons = ({ filterItem, setItem, menuItems }) => {
  return (
    <>
      <div className="d-flex justify-content-center">
				<div className="btn-groups">
	        {menuItems.map((val, id) => {
	          return (
	            <button
	              className="btn-dark text-white p-1 px-2 mx-5 btn fw-bold"
	              onClick={() => filterItem(val)}
	              key={id}
	            >
	              {val}
	            </button>
	          );
	        })}
	        <button
	          className="btn-dark text-white p-1 px-3 mx-5 fw-bold btn"
	          onClick={() => setItem(foods)}
	        >
	          All
	        </button>

	        {/* <button
	          className="btn-warning text-white p-1 mx-5"
	          onClick={() => filterItem("Breakfast")}
	        >
	          Breakfast
	        </button>
	        <button
	          className="btn-warning text-white p-1 px-2 mx-5"
	          onClick={() => filterItem("Lunch")}
	        >
	          Lunch
	        </button>
	        <button
	          className="btn-warning text-white p-1 mx-5"
	          onClick={() => filterItem("Dinner")}
	        >
	          Dinner
	        </button> */}
				</div>
      </div>
    </>
  );
};

const Card = ({ item }) => {
  return (
    <>
      <div className="container-fluid">
        <div className="row justify-content-center">
          {item.map((val) => {
            return (
              <div
                className="col-md-4 col-sm-6 card my-3 py-3 border-0"
                key={val.id}
              >
                <div className="card-img-top text-center">
                  <img src={val.img} alt={val.title} className="photo w-75" />
                </div>
                <div className="card-body">
                  <div className="card-title fw-bold fs-4">
                    {val.title} &nbsp;&nbsp;&nbsp;&nbsp;--&nbsp;&nbsp;
                    {val.price}
                  </div>
                  <div className="card-text">{val.desc}</div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
};

const Food = () => {
  const [item, setItem] = useState(foods);

  const menuItems = [...new Set(foods.map((val) => val.category))];

  const filterItem = (curcat) => {
    const newItem = foods.filter((newval) => {
      return newval.category === curcat;
    });
    setItem(newItem);
  };
  return (
    <main id="main">
			<Breadcrumb page="Foods" />
      <div className="container-fluid">
        <div className="row">
          <h1 className="col-12 text-center my-3 fw-bold">Food Filtering App</h1>
          <Buttons
            filterItem={filterItem}
            setItem={setItem}
            menuItems={menuItems}
          />
          <Card item={item} />
        </div>
      </div>
    </main>
  );
};

export default Food;
